import React, { useState, useEffect } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Select, SelectItem } from "@/components/ui/select";
import { Button } from "@/components/ui/button";

const Catalog = () => {
  const [products, setProducts] = useState([]);
  const [search, setSearch] = useState("");
  const [category, setCategory] = useState("");
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    fetch("http://localhost:3030/api/catalog")
      .then((res) => res.json())
      .then((data) => {
        setProducts(data);
        setFilteredProducts(data);
        const uniqueCategories = [...new Set(data.flatMap((p) => p.category))];
        setCategories(uniqueCategories);
      });
  }, []);

  useEffect(() => {
    let filtered = products.filter((p) =>
      p.sku.toLowerCase().includes(search.toLowerCase()) ||
      p.descriptions.some((desc) => desc.toLowerCase().includes(search.toLowerCase()))
    );

    if (category) {
      filtered = filtered.filter((p) => p.category.includes(category));
    }

    setFilteredProducts(filtered);
  }, [search, category, products]);

  return (
    <div className="p-4">
      <div className="flex gap-4 mb-4">
        <Input
          placeholder="Search..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          className="w-1/2"
        />
        <Select onValueChange={setCategory}>
          <SelectItem value="">All Categories</SelectItem>
          {categories.map((cat) => (
            <SelectItem key={cat} value={cat}>{cat}</SelectItem>
          ))}
        </Select>
      </div>
      <div className="grid grid-cols-3 gap-4">
        {filteredProducts.map((product) => (
          <Card key={product.sku} className="p-2">
            <CardContent>
              <img
                src={product.image ? `http://localhost:3030${product.image}` : "placeholder.jpg"}
                alt={product.sku}
                className="w-full h-40 object-cover"
              />
              <h2 className="font-bold mt-2">{product.sku}</h2>
              <p className="text-sm">{product.descriptions.join(", ")}</p>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
};

export default Catalog;
